INSERT INTO "public"."dishes" ("id", "name", "default_serving", "description", "store_id", "eating_style", "steps", "meal_type", "goal", "health_condition", "image_url") VALUES ('1', 'Kare-Kare ', '1', 'A Filipino stew made with oxtail, vegetables, and a rich peanut sauce, often served with shrimp paste.', null, 'Balanced', '["Boil oxtail, tripe, and/or beef until tender. Set aside.","In a pot, sauté garlic and onions.","Add sautéed veggies (eggplant, string beans, banana flower, pechay) and beef broth.","Mix in peanut butter or ground peanuts; simmer.","Season with salt and fish sauce.","Serve with bagoong (fermented shrimp paste)."]', 'Lunch/Dinner', '["Improve physical health","Boost energy","Managing stress","Eating a balanced diet"]', '["Diabetes","High blood pressure","Heart disease","Kidney disease"]', 'https://exscmqdazkrtrfhstytk.supabase.co/storage/v1/object/public/dishes/download%20(12).jpg'), ('2', 'Chicken Afritada', '1', 'A tomato-based chicken or pork stew with potatoes, carrots, and bell peppers.', null, 'Balanced', '["Sauté garlic, onions, and tomatoes.","Add chicken or pork; cook until lightly browned.","Pour in tomato sauce and water; simmer until meat is tender.","Add potatoes, carrots, and bell peppers; cook until vegetables are soft.","Season with salt and pepper. Serve hot."]', 'Lunch/Dinner', '["Improve physical health","Boost energy","Managing stress","Eating a balanced diet"]', '["Diabetes","High blood pressure","Heart disease","Kidney disease"]', 'https://exscmqdazkrtrfhstytk.supabase.co/storage/v1/object/public/dishes/afritada.jpg'), ('3', 'Pork Mechado', '1', 'A savory beef stew braised in soy sauce, tomatoes, and spices, often with a strip of pork fat.', null, 'Balanced', '["Marinate beef in soy sauce, calamansi, and garlic for 30 mins.","Sear meat in oil until brown.","Sauté onions and garlic, add tomato sauce.","Add beef, water, bay leaves; simmer until tender.","Add potatoes and carrots; season with salt and pepper."]', 'Lunch/Dinner', '["Improve physical health","Boost energy","Managing stress","Eating a balanced diet"]', '["Diabetes","High blood pressure","Heart disease","Kidney disease"]', 'https://exscmqdazkrtrfhstytk.supabase.co/storage/v1/object/public/dishes/mechadu.jpg'), ('4', 'Bicol Express', '1', 'A spicy coconut milk-based dish with pork and chili peppers, originating from the Bicol region.', null, 'Keto, Low-Carbs', '["Sauté garlic, onions, and ginger.","Add sliced pork; cook until browned.","Add chili peppers and shrimp paste.","Pour in coconut milk; simmer until pork is tender and sauce thickens.","Serve hot."]', 'Lunch/Dinner', '["Improve physical health","Boost energy","Managing stress","Eating a balanced diet"]', '["Diabetes","High blood pressure","Heart disease","Kidney disease"]', 'https://exscmqdazkrtrfhstytk.supabase.co/storage/v1/object/public/dishes/bicol.jpg'), ('5', 'Grilled Liempo', '1', 'Marinated pork belly grilled to perfection, often served with dipping sauces.', null, 'Keto, High Protein', '["Marinate pork belly in soy sauce, calamansi, garlic, and brown sugar for 2-3 hours.","Grill on medium heat until cooked, turning occasionally.","Serve with dipping sauce of vinegar, garlic, and chili."]', 'Lunch/Dinner', '["Weight loss","Improve physical health","Optimized athletic performance","Eating a balanced diet"]', '["High blood pressure","Heart disease","Kidney disease"]', 'https://exscmqdazkrtrfhstytk.supabase.co/storage/v1/object/public/dishes/liempo.jpg'), ('6', 'Chicken Inasal', '1', 'A popular Filipino grilled chicken marinated in a mixture of vinegar, calamansi, garlic, and spices. Known for its smoky flavor and juicy texture.', null, 'High Protein, Low-Carbs', '["Marinate chicken in calamansi juice, vinegar, garlic, lemongrass, and annatto oil for at least 3 hours.","Grill over charcoal or oven until cooked.","Baste occasionally with marinade while grilling."]', 'Lunch/Dinner', '["Weight loss","Improve physical health","Optimized athletic performance","Managing stress","Eating a balanced diet"]', '["Diabetes","High blood pressure","Heart disease","Kidney disease"]', 'https://exscmqdazkrtrfhstytk.supabase.co/storage/v1/object/public/dishes/inasal.jpg'), ('7', 'Daing na Bangus', '1', 'Milkfish marinated in vinegar, garlic, and pepper, then fried until crispy. Usually enjoyed with rice and a side of tomatoes.', null, 'High Protein, Low-Carbs', '["Marinate bangus in vinegar, garlic, salt, and pepper for 2-3 hours.","Pat dry and pan-fry until golden and crispy.","Serve with rice and vinegar dipping sauce."]', 'Breakfast', '["Weight loss","Improve physical health","Optimized athletic performance","Eating a balanced diet"]', '["High blood pressure","Kidney disease"]', 'https://exscmqdazkrtrfhstytk.supabase.co/storage/v1/object/public/dishes/daing.jpg'), ('8', 'Pritong Tilapia', '1', 'Whole tilapia fried to a golden crisp, keeping the meat tender inside. Often served with a simple dipping sauce or calamansi.', null, 'High Protein, Low-Carbs', '["Clean tilapia and season with salt and pepper.","Heat oil and fry until golden brown.","Serve with rice and dipping sauce."]', 'Breakfast/Lunch', '["Weight loss","Improve physical health","Optimized athletic performance","Eating a balanced diet"]', '["Diabetes","Heart disease","Kidney disease"]', 'https://exscmqdazkrtrfhstytk.supabase.co/storage/v1/object/public/dishes/prito%20tilapia.jpg'), ('9', 'Laing', '1', 'Dried taro leaves cooked in rich coconut milk with chili peppers, garlic, and shrimp or meat. Served as a side or main dish.', null, 'Keto, Low-Carbs', '["Sauté garlic, onion, and ginger.","Add coconut milk, dried taro leaves, and chili peppers.","Simmer until leaves are tender and sauce thickens.","Add cooked meat or shrimp if desired."]', 'Lunch/Dinner', '["Weight loss","Improve physical health","Eating a balanced diet"]', '["Diabetes","Heart disease","Kidney disease"]', 'https://exscmqdazkrtrfhstytk.supabase.co/storage/v1/object/public/dishes/laing.jpg'), ('10', 'Ginisang Ampa